@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

html {
  /* Colors */
  --color-accent: #819DB1; 
  --font-primary: "Bespoke Serif", serif;
  --font-secondary: 'Open Sans', sans-serif; 

  --color-accent-gray: #f3f3f3;
  --color-shade: #f3f3f3;

  --color-text-secondary: #2d3339;

  /* Default property values */
  --background: white;
  --color: #222;
  --padding: 6rem 2rem;
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --max-width: 900px;
  --border-color: #c6ccd2;
  --transition-time: 0.1s;
  --transition: var(--transition-time) color,
    var(--transition-time) background-color, var(--transition-time) border-color,
    var(--transition-time) text-decoration-color,
    var(--transition-time) box-shadow, var(--transtion-time) transform;

  /* Dark theme */
