}

.primo-page {
  color: var(--color);
  line-height: 1.6;
  font-size: 1rem;
  background: var(--background);
  font-family: var(--font-secondary); 

  hr {
    border-color: var(--border-color);
  }
}

.primo-section.has-content {
  background: var(--color-shade); 
}

.primo-section .primo-content {
  max-width: var(--max-width);
  margin: 0 auto;
  padding: var(--padding);

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  p {
    color: var(--color-text-secondary);
    padding: 0.25rem 0;
    line-height: 1.75;
  }

  a {
    text-decoration: underline;
  }

  h1 {
    font-family: var(--font-primary);
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 1rem;
  }

  h2 {
    font-family: var(--font-primary);
    font-size: 1.75rem;
    margin-bottom: 0.5rem;
    font-weight: 500;

    &:after {
      width: 66px;
      border-bottom: 2px solid var(--color);
      content: "";
      display: block;
      margin-bottom: 1.5rem;
      margin-top: 0.5rem;
    }
  }

  h3 {
    font-family: var(--font-primary);
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container {
  max-width: var(--max-width, 1200px);
  margin: 0 auto;
  padding: var(--padding);
}

.heading {
  font-family: var(--font-primary);
  font-size: 1.75rem;
  line-height: 1;
  font-weight: 500;

  &:after {
    width: 66px;
    border-bottom: 2px solid var(--color);
    content: "";
    display: block;
    padding-top: 1rem;
    margin-bottom: 1.5rem;
  }
}

.link {
  border-bottom: 1px solid var(--color);
  transition: 0.1s border-color;

  &:hover {
    border-color: transparent;
  }
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent);
    border: 2px solid var(--color-accent);
  }
}

.section-container {
  display: grid;
}

.breadcrumbs {
  display: flex;
  margin-bottom: 3rem; 

  & > *:not(:last-child):after {
    content: "/";
    display: inline;
    margin-left: 0.75rem;
    margin-right: 0.75rem;
  }
}

.article-info {
  display: grid;
  gap: 0.5rem; 

  .date {
    font-size: 0.75rem; 
  }

  & > div {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1rem;
  }
}

.heading {
  &:after {
    display: none;
  }
}

.links {
  display: flex;
  gap: 1rem;

  a {
    display: grid;
    grid-template-columns: auto auto;
    gap: 0.5rem;
    place-items: center;
  }
}

img {
  margin-top: 1.5rem;
  width: 100%;
  border-radius: var(--border-radius);
  box-shadow: var(--box-shadow); 
}

@import url("https://unpkg.com/modern-normalize@1.1.0/modern-normalize.css");

