figure {
  position: relative;
  margin-left: 18px;

  &.square {
    margin-left: 0; 
    img {
      border-radius: var(--border-radius, 2rem);
    }
    svg {
      display: none; 
    }
  }

  img {
    height: 226px;
    width: 226px;
    border-radius: 50%;
    z-index: 1;
    position: relative;
    object-fit: cover;
  }

  svg {
    position: absolute;
    top: 0;
    left: -18px;
    visibility: hidden;

    circle { 
      fill: var(--color-accent);
      transition: 1s transform;
      height: 100%;
      width: 100%;
    }

    circle:nth-child(2) {
      filter: brightness(0.75);
    }

    circle:nth-child(3) {
      filter: brightness(0.5);
    }

    &.show {
      visibility: visible; 

      circle:nth-child(1) {
        transform: translateX(-18px);
      }

      circle:nth-child(2) {
        transform: translateX(-12px);
      }

      circle:nth-child(3) {
        transform: translateX(-6px);
      }
    }
  }
}

@media (min-width: 900px) {
  .section-container {
    gap: 6rem;
    grid-template-columns: auto auto;

    & > figure {
      grid-column: 2;
    }

    & > div {
      place-content: center;
      grid-row: 1;
    }
  }
}
